mapper.insert:
insert into aik_commonProblem (id, question, answer, 
  fileUrl, createDate, updateDate
  )
values (#{id,jdbcType=INTEGER}, #{question,jdbcType=VARCHAR}, #{answer,jdbcType=VARCHAR}, 
  #{fileUrl,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, #{updateDate,jdbcType=TIMESTAMP}
  )

mapper.select:
select question, answer from aik_commonProblem where 1=1
and locate(#{searchDesc}, question) > 0
order by id desc limit ${(page-1)*size}, #{size}
