mapper.select:
select

from diet_dailyDietPlan
where 1=1

  and userId = #{userId}


  and recordDate = DATE(#{recordDate})


  and dietType = #{dietType}

select dp.id, df.image, df.name, dp.weight/df.weight as number, df.weightUnit, dp.weight from diet_dailyDietPlan dp inner join diet_food df on dp.foodId = df.id where 1=1
and userId = #{userId}
and recordDate = DATE(#{recordDate})
and dietType = #{dietType}

select

from diet_dailyDietPlan
where 1=1

  and id = #{id}


  and userId = #{userId}


  and foodId = #{foodId}

select
t.`id` id , m.`id` foodId, m.`name` foodName, a.`id` userId, a.`userName` userName, t.`recordDate` recordDate , t.`dietType` dietType,t.`weight` weight
FROM diet_dailyDietPlan t , diet_food m  , acc_userAccount a
where m.`id` = t.`foodId` AND a.`id` = t.`userId`

  and t.id = #{id}


  and t.userId = #{userId}


  and m.name = #{foodName}


  and t.foodId = #{foodId}


  and a.name = #{userName}


     and DATE_FORMAT(t.recordDate,'%Y-%m-%d') >= #{starttime}



     and DATE_FORMAT(t.recordDate,'%Y-%m-%d') <= #{endtime}
